initial.code:
const mail = context.panel.elements.find((element) => element.id === "email");

mail.value = "${__user.email}"
context.panel.enableSubmit()

resetAction.code:
if (context.panel.response) {
  context.grafana.notifySuccess(['Update', 'Values updated successfully.']);
  context.grafana.locationService.reload();
} else {
  context.grafana.notifyError(['Update', 'An error occurred updating values.']);
}

update.code:
if (context.panel.response && context.panel.response.ok) {
  context.grafana.notifySuccess(['Export', 'The monthly report will be emailed to you in a few minutes']);
  context.grafana.locationService.reload();
} else {
  context.grafana.notifyError(['Update', 'An error occured updating values.']);
}

update.getPayload:
const payload = {};

context.panel.elements.forEach((element) => {
  if (!element.value) {
    return;
  }

  payload[element.id] = element.value;
})

payload['code'] = '${Code}';
payload['login'] = '${__user.login}';
payload['year'] = '${Year}';
payload['month'] = '${Month}';
return payload;

